import { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Switch } from '@/components/ui/switch';
import { cmsStorage } from '@/lib/cms-storage';
import { ArrowLeft, Save } from 'lucide-react';

export default function ProductForm() {
  const navigate = useNavigate();
  const { id } = useParams();
  const isEdit = !!id;

  const [formData, setFormData] = useState({
    name: '',
    slug: '',
    category: '',
    price: '',
    discountPrice: '',
    stock: '',
    unit: '',
    description: '',
    features: '',
    image: '',
    isFeatured: false,
    metaTitle: '',
    metaDescription: '',
    metaKeywords: '',
  });

  useEffect(() => {
    if (localStorage.getItem('admin_logged_in') !== 'true') {
      navigate('/admin/login');
      return;
    }

    if (isEdit && id) {
      const product = cmsStorage.getProduct(id);
      if (product) {
        setFormData({
          name: product.name,
          slug: product.slug,
          category: product.category,
          price: product.price.toString(),
          discountPrice: product.discountPrice?.toString() || '',
          stock: product.stock.toString(),
          unit: product.unit,
          description: product.description,
          features: product.features.join('\n'),
          image: product.image,
          isFeatured: product.isFeatured,
          metaTitle: product.meta.title,
          metaDescription: product.meta.description,
          metaKeywords: product.meta.keywords.join(', '),
        });
      }
    }
  }, [id, isEdit, navigate]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const productData = {
      name: formData.name,
      slug: formData.slug || formData.name.toLowerCase().replace(/\s+/g, '-'),
      category: formData.category,
      price: parseFloat(formData.price),
      discountPrice: formData.discountPrice ? parseFloat(formData.discountPrice) : undefined,
      stock: parseInt(formData.stock),
      unit: formData.unit,
      description: formData.description,
      features: formData.features.split('\n').filter(f => f.trim()),
      image: formData.image,
      isFeatured: formData.isFeatured,
      meta: {
        title: formData.metaTitle,
        description: formData.metaDescription,
        keywords: formData.metaKeywords.split(',').map(k => k.trim()),
      },
    };

    if (isEdit && id) {
      cmsStorage.updateProduct(id, productData);
    } else {
      cmsStorage.saveProduct(productData);
    }

    navigate('/admin/products');
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-white border-b sticky top-0 z-10">
        <div className="container mx-auto px-4 py-4">
          <div className="flex items-center gap-4">
            <Button variant="ghost" onClick={() => navigate('/admin/products')}>
              <ArrowLeft className="h-4 w-4 mr-2" />
              Kembali
            </Button>
            <h1 className="text-2xl font-bold">
              {isEdit ? 'Edit Produk' : 'Tambah Produk Baru'}
            </h1>
          </div>
        </div>
      </header>

      <div className="container mx-auto px-4 py-8 max-w-4xl">
        <form onSubmit={handleSubmit}>
          <Card className="mb-6">
            <CardHeader>
              <CardTitle>Informasi Produk</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
