-- Table: Product Categories
CREATE TABLE IF NOT EXISTS app_d7e3b9e33c_product_categories (
  id UUID DEFAULT gen_random_uuid() PRIMARY KEY,
  name TEXT NOT NULL UNIQUE,
  slug TEXT NOT NULL UNIQUE,
  description TEXT,
  created_at TIMESTAMP WITH TIME ZONE DEFAULT TIMEZONE('utc'::text, NOW()) NOT NULL
);

-- Table: Products
CREATE TABLE IF NOT EXISTS app_d7e3b9e33c_products (
  id UUID DEFAULT gen_random_uuid() PRIMARY KEY,
  name TEXT NOT NULL,
  slug TEXT NOT NULL UNIQUE,
  category_id UUID REFERENCES app_d7e3b9e33c_product_categories NOT NULL,
  price DECIMAL(10,2) NOT NULL,
