  meta_keywords JSONB NOT NULL DEFAULT '[]'::jsonb,
  created_by UUID REFERENCES app_d7e3b9e33c_admins,
  created_at TIMESTAMP WITH TIME ZONE DEFAULT TIMEZONE('utc'::text, NOW()) NOT NULL,
  updated_at TIMESTAMP WITH TIME ZONE DEFAULT TIMEZONE('utc'::text, NOW()) NOT NULL
);

-- Table: Blog Categories
CREATE TABLE IF NOT EXISTS app_d7e3b9e33c_blog_categories (
  id UUID DEFAULT gen_random_uuid() PRIMARY KEY,
  name TEXT NOT NULL UNIQUE,
  slug TEXT NOT NULL UNIQUE,
  description TEXT,
  created_at TIMESTAMP WITH TIME ZONE DEFAULT TIMEZONE('utc'::text, NOW()) NOT NULL
);

-- Table: Blogs
CREATE TABLE IF NOT EXISTS app_d7e3b9e33c_blogs (
