CREATE INDEX IF NOT EXISTS idx_marketplace_clicks_product ON app_d7e3b9e33c_marketplace_clicks(product_id);

-- Setup Row Level Security (RLS)
ALTER TABLE app_d7e3b9e33c_admins ENABLE ROW LEVEL SECURITY;
ALTER TABLE app_d7e3b9e33c_product_categories ENABLE ROW LEVEL SECURITY;
ALTER TABLE app_d7e3b9e33c_products ENABLE ROW LEVEL SECURITY;
